#define PROGRAM_NUM_OF_PARA 44

// private variables
static const uint8_t program_midi_msg[2][PROGRAM_NUM_OF_PARA];
static const uint8_t program_midi_val[128][PROGRAM_NUM_OF_PARA];

stm32synth_res_t stm32synth_program_set(stm32synth_config_t *_config, uint8_t _ch, stm32synth_program_list_t _program)
{
	stm32synth_res_t res = STM32SYNTH_RES_OK;

	if(_ch == STM32SYNTH_MIDINN_DRUMCH)
	{
		return res;
	}

	stm32synth_program_clear(_config, _ch);

	uint8_t midicmd[4];
	for (uint8_t pp = 1; pp < PROGRAM_NUM_OF_PARA; pp++) // skip volume
	{
		midicmd[0] = 0x10;
		midicmd[1] = ((program_midi_msg[0][pp] & 0x0F) << 4) | (_ch & 0x0F);
		midicmd[2] = program_midi_msg[1][pp];
		midicmd[3] = program_midi_val[_program][pp];

		stm32synth_component_inputmidi(_config, midicmd);
	}

	return res;
}

stm32synth_res_t stm32synth_program_clear(stm32synth_config_t *_config, uint8_t _ch)
{
	stm32synth_res_t res = STM32SYNTH_RES_OK;

	if(_ch == STM32SYNTH_MIDINN_DRUMCH)
	{
		return res;
	}

	// LFO
	stm32synth_config_lfo_t initLfo;
	initLfo.amp = 0;
	initLfo.freq = 0;
	initLfo.type = STM32SYNTH_LFO_SIN;
	initLfo.para.duty = 0.5f;

	//_config->volume[_ch] = 0.0f;
	_config->pitch[_ch] = 0;
	_config->distortion[_ch] = 128;

	_config->waveform[_ch][STM32SYNTH_WAVEFORM_0].sin_level = 0.0f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_0].squ_level = 0.0f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_0].squ_duty = 0.5f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_0].tri_level = 0.0f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_0].tri_peak = 0.5f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_0].pitch = 0;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_1].sin_level = 0.0f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_1].squ_level = 0.0f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_1].squ_duty = 0.5f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_1].tri_level = 0.0f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_1].tri_peak = 0.5f;
	_config->waveform[_ch][STM32SYNTH_WAVEFORM_1].pitch = 0;

	_config->adsr[_ch].attack_level = 2.0f;
	_config->adsr[_ch].attack_ms = 50;
	_config->adsr[_ch].decay_ms = 0;
	_config->adsr[_ch].release_ms = 500;

	_config->tre[_ch] = initLfo;
	_config->vib[_ch] = initLfo;

#ifdef STM32SYNTH_FILTER
#ifdef STM32SYNTH_CHORDFILTER
	//_config->lpf[_ch].cutoff_freq_nn.relative = 120 - 0x40;
	_config->filter[_ch].cutoff_freq_nn.absolute = 127 << 8;
	_config->filter[_ch].q_factor = 0.8f;
	_config->wow[_ch] = initLfo;
#endif
#endif

	_config->envelope[_ch].volume.finish_value = 64;
	_config->envelope[_ch].volume.time_ms = 0;
	_config->envelope[_ch].freq.finish_value = 64;
	_config->envelope[_ch].freq.time_ms = 0;
	_config->envelope[_ch].cutoff.finish_value = 64;
	_config->envelope[_ch].cutoff.time_ms = 0;

	_config->phonic[_ch] = STM32SYNTH_PHONIC_POLY_SUSTON;

	return res;
}

